var servlets = {};

this.route = function route(request, response, url) {
   var servlet = servlets[url.pathname];

   if (!servlet) {
      response.end();

      return;
   }

   console.log('routed request to ' + servlet.name);

   servlet.onRequest(request, response, url);
};

this.addServlet = function addServlet(path, servlet) {
   servlets[path] = servlet;
};
