this.SERVLET = {
   name: 'default-servlet',

   getUser: function getUser(request, response, url) {
      
   },

   handleError: function handleError(request, response, url, statusCode, statusMessage) {
      response.writeHead(statusCode);
      response.write(statusMessage);
      response.end();
   },

   handleRedirect: function handleRedirect(request, response, url, redirectUrl) {
      response.writeHead(301, {'Location': redirectUrl});
   },

   onRequest: function onRequest(request, response, url) {
      response.write('Hello');
      response.end();
   }
};
